// src/App.js
import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css"; // Import custom styles
import DataTable from "./components/DataTable";
import CategoryList from "./components/CategoryList";

function App() {
  const [data, setData] = useState([]);
  const [category, setCategory] = useState("headphones");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/data/${category}`);
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error("Error fetching data:", error);
        setData([]);
      }
    };
    fetchData();
  }, [category]);

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Amazon Scraper</h1>
      <div className="row">
        <div className="col-md-3">
          <CategoryList setCategory={setCategory} selectedCategory={category} />
        </div>
        <div className="col-md-9">
          <DataTable data={data} />
        </div>
      </div>
    </div>
  );
}

export default App;
